import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { ActionTypes } from "./containers/redux/constants/action-types";

export const PostForm = (props) => {
  const [content, setContent] = useState("");
  const dispatch = useDispatch();
  const posts = useSelector((state) => state.posts); // Assuming you have a "posts" reducer
  const [allPosts, setAllPosts] = useState([]);
  useEffect(()=>{
    fetch("/forum")
    .then(r=>r.json())
    .then(posts=>setAllPosts(posts))
  },[])

  const displayPosts = allPosts.map((post) => {
    return (
        <div>
        <div>post.title
        </div></div>
    )
  })

  function handleSubmit(e) {
    e.preventDefault();
    fetch("/forum", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: title,
        content: content,
      }),
    })
      .then((r) => r.json())
      .then((newPost) =>
        dispatch({ type: ActionTypes.SET_POST, payload: newPost })
      );
  }

  return (
    <div>
      <h1>Welcome to the Forums</h1>
      <label>
        <h3>Start a post here!:</h3>
        <form onSubmit={handleSubmit}>
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          ></input>
          <button type="submit">Submit</button>
        </form>
      </label>

      <div>
        {posts && posts.map((post) => (
          <div key={post.id}>
            <h2>{post.title}</h2>
            <p>{post.author}</p>
            <p>{post.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
};
